sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here
    var user_name = request.pathParams.user_name;
    var query = request.queryParams.ni_query;
    var requesUser = new GlideRecord("sys_user");
    requesUser.get("user_name", user_name);

    if (!requesUser.user_name) {
        var apiError = new sn_ws_err.ServiceError();
        apiError.setStatus(542);
        apiError.setMessage("User not found");
        apiError.setDetail("No user record found for the user_name passed into the ni_info web service resource using the user_name path parameter.");
        response.setError(apiError);
        return;
    }

    var userCount = new GlideAggregate("x_58872_needit_needit");
    userCount.addAggregate("Count");
    userCount.addQuery("u_requested_for", requesUser.sys_id);
    userCount.addEncodedQuery(query);
    userCount.groupBy("u_request_type");
    userCount.query();

    var count = 0;
    var resList = [];
    var totalcount = 0;

    while (userCount.next()) {
        count = userCount.getAggregate("Count");
        var userId = userCount.u_request_type.getDisplayValue();
        resList.push({
            "userid": userId,
            "count": count
        });
        totalcount = parseInt(totalcount) + parseInt(count);
    }
    var body = {};

    body.totalcount = totalcount;
    body.resList = resList;
    body.user = {
        "userId": requesUser.user_name,
        "sys_id": requesUser.sys_id
    };

    response.setBody(body);


})(request, response);
